"use strict";
// Javascript처럼 Typescript의 모든 숫자는 부동 소수점 값
// Typescript는 16진수 및 10진수 리터럴 외에도
// ECMAScript 2015에 도입된 2진수 및 8진수를 지원한다.
// 10진수
var decimal = 6;
// 16진수
var hex = 0xf00d;
// 2진수
var binary = 10;
// 8진수
var octal = 484;
// NaN
var notANumber = NaN;
// 수치 표기 가능
var underscoreNum = 1000000;
